public void function() throws Exception {
     int port = 9090;
     HttpProxyServer proxyServer = new DefaultHttpProxyServer(port);
     proxyServer.start();

     Proxy proxy = new Proxy();
     proxy.setProxyType(Proxy.ProxyType.MANUAL);
     String proxyStr = String.format("localhost:%d", port);
     proxy.setHttpProxy(proxyStr);
     proxy.setSslProxy(proxyStr);

     DesiredCapabilities capability = DesiredCapabilities.firefox();
     capability.setCapability(CapabilityType.PROXY, proxy);

     final String urlString = "http://www.yahoo.com/";

     // Note this will actually launch a browser!!
     final WebDriver driver = new FirefoxDriver(capability);
     driver.manage().timeouts().pageLoadTimeout(30, TimeUnit.SECONDS);

     driver.get(urlString);
     final String source = driver.getPageSource();

     // Just make sure it got something within reason.
     assertTrue(source.length() > 100);
     driver.close();

     proxyServer.stop();
 }

public static void function( AtomicInteger nextTest, int tests ) {
     String baseUrl = "https://localhost:8443/benchmark/BenchmarkTest";
     FirefoxDriver driver = new FirefoxDriver();
     JavascriptExecutor js = (JavascriptExecutor) driver;

     while( nextTest.get() <= tests ) {
         String test = "000000" + nextTest.getAndIncrement();
         test = test.substring(test.length() - 5);

         try {
             String url = baseUrl + test + ".html";
             System.out.println("URL: " + url);
             driver.get(url);

             // get URL, find all buttons, and click them
             for ( WebElement we : driver.findElements(By.tagName("form"))) {
                 we.submit();
             }

             // reload page, and if there's a javascript button, click that
             driver.get(url);
             Thread.sleep(500);
             String jQuerySelector = "#login-btn";
             js.executeScript("$(\"" + jQuerySelector + "\").click();");
             Thread.sleep(500);
         } catch (Exception e) {
             // This is expected since many pages do not have the jquery button
             // System.err.println("  -> ERROR: " + e.getMessage());
         }
     }

     driver.quit();
 }

