public void function() throws Exception {
   OAuthService service = new ServiceBuilder().provider(OpenConextApi20Implicit.class)
       .apiKey(OAUTH_KEY.concat(UUID.randomUUID().toString())).apiSecret(OAUTH_SECRET.concat("force_consent"))
       .callback(OAUTH_CALLBACK_URL).scope(OAUTH_OPENCONEXT_API_READ_SCOPE).build();
   String authUrl = service.getAuthorizationUrl(null);
   LOG.debug("Auth url: {}", authUrl);
   getWebDriver().get(authUrl);
   loginAtMujinaIfNeeded(USER_ID);

   // Deny on user consent page
   WebElement authorizeButton = getWebDriver().findElement(By.id("decline_terms_button"));
   authorizeButton.click();

   URI uri = URI.create(getWebDriver().getCurrentUrl());
   LOG.debug("URL is: " + uri.toString());
   LOG.debug("Response body is: " + getWebDriver().getPageSource());
   callbackRequestFragment = uri.getFragment();
   assertNotNull("redirect URL should contain fragment.", callbackRequestFragment);
   assertFalse("redirect URL fragment should not contain access token", callbackRequestFragment.contains("access_token="));
   assertFalse("redirect URL fragment should contain access token", callbackRequestFragment.contains("access_token="));
 }

