     }

     for (String[] curEntry : initialUrls) {

         String pulledUrl = pullCompanyUrl(driver, curEntry, key);
         if (pulledUrl != null) {
             urls.add(pulledUrl);
             System.out.println(pulledUrl);
             //PersistenceImpl_WriteToFile.appendToFile(fileOutput, pulledUrl + "\n");
             persistEntry(pulledUrl);
         }
     }
 }

