public void function(final String redirectUris, final String redirectUri) throws Exception {
     showTitle("OC5:FeatureTest-Displays Policy in Login Page");

     List<ResponseType> responseTypes = Arrays.asList(ResponseType.CODE);
     String policyUri = "http://www.gluu.org/policy";

     // 1. Register client
     RegisterRequest registerRequest = new RegisterRequest(ApplicationType.WEB, "oxAuth test app",
             StringUtils.spaceSeparatedToList(redirectUris));
     registerRequest.setResponseTypes(responseTypes);
     registerRequest.setPolicyUri(policyUri);

     RegisterClient registerClient = new RegisterClient(registrationEndpoint);
     registerClient.setRequest(registerRequest);
     RegisterResponse registerResponse = registerClient.exec();

     showClient(registerClient);
     assertEquals(registerResponse.getStatus(), 200, "Unexpected response code: " + registerResponse.getEntity());
     assertNotNull(registerResponse.getClientId());
     assertNotNull(registerResponse.getClientSecret());
     assertNotNull(registerResponse.getRegistrationAccessToken());
     assertNotNull(registerResponse.getClientIdIssuedAt());
     assertNotNull(registerResponse.getClientSecretExpiresAt());

     String clientId = registerResponse.getClientId();

     // 2. Request authorization and receive the authorization code.
     List<String> scopes = Arrays.asList("openid", "profile", "address", "email");
     String state = UUID.randomUUID().toString();

     AuthorizationRequest authorizationRequest = new AuthorizationRequest(responseTypes, clientId, scopes, redirectUri, null);
     authorizationRequest.setState(state);

     String authorizationRequestUrl = getAuthorizationEndpoint() + "?" + authorizationRequest.getQueryString();

     AuthorizeClient authorizeClient = new AuthorizeClient(getAuthorizationEndpoint());
     authorizeClient.setRequest(authorizationRequest);

     try {
         startSelenium();
         driver.navigate().to(authorizationRequestUrl);

         WebElement policy = driver.findElement(By.xpath("//a[@href='" + policyUri + "']"));
         assertNotNull(policy);
     } catch (NoSuchElementException ex) {
         fail("Policy not found");
     } finally {
         stopSelenium();
     }
 }

public void function() throws Exception {
   assumeTrue(domainHelper.checkHasValidAlternateHostname());

   Cookie cookie1 = new Cookie.Builder("fish1", "cod")
       .domain(appServer.getHostName()).build();
   Cookie cookie2 = new Cookie.Builder("fish2", "tune")
       .domain(appServer.getAlternateHostName()).build();

   String url1 = domainHelper.getUrlForFirstValidHostname("/common");
   String url2 = domainHelper.getUrlForSecondValidHostname("/common");

   WebDriver.Options options = driver.manage();

   options.addCookie(cookie1);
   assertCookieIsPresentWithName(cookie1.getName());

   driver.get(url2);
   options.addCookie(cookie2);
   assertCookieIsNotPresentWithName(cookie1.getName());
   assertCookieIsPresentWithName(cookie2.getName());

   driver.get(url1);
   assertCookieIsPresentWithName(cookie1.getName());
   assertCookieIsNotPresentWithName(cookie2.getName());

   options.deleteAllCookies();
   assertCookieIsNotPresentWithName(cookie1.getName());

   driver.get(url2);
   assertCookieIsPresentWithName(cookie2.getName());
 }

